from typing import Counter


def longestPalindrome(s: str) -> int:
    counter = sorted(Counter(s).items(), key=lambda x: x[1], reverse=True)
    print(Counter(s))
    res = 0
    flag = True
    for _, x in counter:
        if x % 2 == 0:
            res += x
        else:
            if flag:
                flag = False
                res += x
            else:
                res += x-1

    return res


s = ""
print(longestPalindrome(s))
